import { cn } from "@/lib/cn"
import { Separator as SeparatorPrimitive } from "@kobalte/core"
import type { VoidComponent } from "solid-js"
import { splitProps } from "solid-js"

export const Separator: VoidComponent<SeparatorPrimitive.SeparatorRootProps> = (
  props
) => {
  const [local, rest] = splitProps(props, ["class"])
  return (
    <SeparatorPrimitive.Root
      class={cn(
        "bg-border shrink-0 data-[orientation=horizontal]:h-[1px] data-[orientation=vertical]:h-full data-[orientation=horizontal]:w-full data-[orientation=vertical]:w-[1px]",
        local.class
      )}
      {...rest}
    />
  )
}
